function date(){let e,n=new Date().toLocaleDateString("en-GB",{weekday:"long",year:"numeric",month:"long",day:"numeric"});document.getElementById("header_date").innerHTML=n}function greet(){let e=Math.floor(new Date().getHours()/6);switch(e){case 0:document.getElementById("header_greet").innerHTML="Good night!";break;case 1:document.getElementById("header_greet").innerHTML="Good morning!";break;case 2:document.getElementById("header_greet").innerHTML="Good afternoon!";break;case 3:document.getElementById("header_greet").innerHTML="Good evening!"}}function loadFunctions(){date(),greet()}